mapper.insert:
INSERT INTO DAILY( 
			DL_ID , TITLE , CRT_DT
			,USR_ID, LIKE_CNT, OVER_ALL
			)
VALUES (
			#{dailyId}, #{title}, SYSDATE
			,'SJM', '0', #{overAll}
		)

mapper.delete:
DELETE
FROM 	DAILY D
WHERE 	D.DL_ID = #{dailyId}

mapper.update:
UPDATE  DAILY
SET     LIKE_CNT = LIKE_CNT + 1
WHERE   DL_ID = #{dailyId}

UPDATE  DAILY
SET     LIKE_CNT = LIKE_CNT - 1
WHERE   DL_ID = #{dailyId}
